// Get the output string from your data
const rawOutput = $json.output || $input.first().json.output;

// Extract JSON content between ```json and ```
const jsonMatch = rawOutput.match(/```json\n([\s\S]*?)\n```/);

if (jsonMatch && jsonMatch[1]) {
  try {
    // Parse the extracted JSON
    const parsedData = JSON.parse(jsonMatch[1]);

    // Clean and transform the data to only include id and response
    const cleanedData = parsedData.map(item => ({
      email_id: item.id,
      response: item.response.replace(/\\n/g, ' ').replace(/\n/g, ' ').trim()
    }));

    return cleanedData;

  } catch (error) {
    console.error('Error parsing JSON:', error);
    return {
      error: 'Failed to parse JSON from output',
      raw_output: rawOutput
    };
  }
} else {
  // Fallback: try to parse the entire output as JSON
  try {
    const parsedData = JSON.parse(rawOutput);
    const cleanedData = parsedData.map(item => ({
      email_id: item.id,
      response: item.response
    }));
    return cleanedData;
  } catch (error) {
    return {
      error: 'No valid JSON found in output',
      raw_output: rawOutput
    };
  }
}

